class Solution {
    int isDivisibleBy7(string &s){
        long long mod=0;
        for(auto it:s){
            mod=(mod*10+(it-'0'))%7;
        }
        return mod==0;
    }
public:
    string largestPalindrome(int n, int k) {
        string ans;
        for(int i=0; i<n; i++)ans+='9';
        if(k==1 || k==3 || k==9)return ans;
        if(k==2){
            ans[n-1]='8';
            ans[0]='8';
            return ans;
        }
        if(k==4){
            if(n==1)return "8";
            ans[0]='8'; ans[1]='8';
            ans[n-1]='8'; ans[n-2]='8';
            return ans;
        }
        if(k==5){
            ans[n-1]='5';
            ans[0]='5';
            return ans;
        }
        if(k==6){
            if(n==1)return "6";
            if(n==2)return "66";
            ans[n-1]='8';
            ans[0]='8';
            if(n%2){
                ans[n/2]='8';
                return ans;
            }
            ans[n/2]='7';
            ans[n/2-1]='7';
            return ans;
        }
        if(k==8){
            if(n==1)return "8";
            if(n==2)return "88";
            ans[0]='8'; ans[1]='8'; ans[2]='8';
            ans[n-1]='8'; ans[n-2]='8'; ans[n-3]='8';
            return ans;
        }
        for(int i=0; i<10; i++){
            string candidate=ans;
            if(n%2==1)candidate[n/2]='9'-i;
            else candidate[n/2-1]=candidate[n/2]='9'-i;
            if(isDivisibleBy7(candidate))return candidate;
        }
        return "";
    }
};
