import sys

sys.set_int_max_str_digits(10**6)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def gen(h, m):
            if len(h)*2<n:
                return h+m*(n-len(h)*2)+h[::-1]
            return h+h[::-1]

        if k==1 or k==3 or k==9:
            return '9'*n

        if k==2:
            if n==1:
                return '8'
            if n==2:
                return '88'
            h = '8'+'9'*max(n//2-1, 0)
            return gen(h, '9')

        if k==4:
            if n<=4:
                return '8'*n
            return '8'*2+'9'*(n-4)+'8'*2

        if k==5:
            if n==1:
                return '5'
            if n==2:
                return '55'
            h = '5'+'9'*max(n//2-1, 0)
            return gen(h, '9')

        if k==6:
            if n==1:
                return '6'
            if n==2:
                return '66'
            h, mod = divmod(n, 2)
            if mod==0:
                return gen('8'+'9'*(h-2),'7')
            else:
                return gen('8'+'9'*(h-1), '8')

        def cal(n):
            if n%2:
                h = '9'*(n//2)
                s = int(h+'0'+h[::-1])
                x = 10**(n//2)
                for i in range(9,-1,-1):
                    if (s+i*x)%7==0:
                        return h+str(i)+h[::-1]
            else:
                h = '9'*(n//2-1)
                s = int(h+'00'+h[::-1])
                x = 10**(n//2-1)
                for i in range(9,-1,-1):
                    if (s+i*11*x)%7==0:
                        return h+str(i)*2+h[::-1]



        if k==7:
            if n==1:
                return '7'
            if n==2:
                return '77'
            return cal(n)

        if k==8:
            if n<=6:
                return '8'*n
            return '8'*3+'9'*(n-6)+'8'*3
