"""
1: 99...99
2: 89...98
3: 99...99
4: 8, 88988
5: 59995
6: 8766678
7: 9x9
8: 88899888
9: 9999

"""
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n <= 4:
            mx = int('9' * n)
            for i in range(mx,-1,-1):
                si = str(i)
                ris = si[::-1]
                if i % k == 0 and si == ris:
                    return str(i)
        if k in [1, 3, 9]:
            return '9' * n
        if k == 2:
            return '8' + '9' * (n - 2) + '8'
        if k == 4:
            return '88' + '9' * (n - 4) + '88'
        if k == 5:
            return '5' + '9' * (n - 2) + '5'
        if k == 6:
            maybe = ['8'] + ['9'] * (n - 2) + ['8']
            rest = sum(map(int, maybe)) % 3
            if rest == 0:
                return ''.join(maybe)
            if n % 2 == 1:
                maybe[n // 2] = str(9 - rest)
            else:
                mid = n // 2
                if rest == 2:
                    maybe[mid] = '8'
                    maybe[mid - 1] = '8'
                else:
                    maybe[mid] = '7'
                    maybe[mid - 1] = '7'
            return ''.join(maybe)
        if k == 8:
            if n <= 6:
                return '8' * n
            return '888' + '9' * (n - 6) + '888'
        if k == 7:        
            ret = [9] * n
            mid = n // 2
            if n % 2 == 1:
                idx = [mid - 1, mid, mid + 1]
                for num in range(999, -1, -1):
                    if 10 <= num < 100:
                        continue
                    s = str(num)
                    if len(s) < 3:
                        s = '0' + s + '0'
                    if s == s[::-1]:
                        for i, j in zip(idx, s):
                            ret[i] = int(j)
                        cur = 0
                        s = ''.join(map(str, ret))
                        flag = 1
                        for i in range(n, 0, -3):
                            tmp = s[max(0, i-3):i]
                            # print(tmp, flag)
                            if tmp:
                                cur += flag * int(tmp)
                            flag = -flag
                        if cur % 7 == 0:
                            return s
            else:
                idx = [mid - 2, mid - 1, mid, mid + 1]
                for num in range(9999, -1, -1):
                    if 100 <= num < 1000:
                        continue
                    if 0 < num < 10:
                        continue
                    if num == 0:
                        s = '0000'
                    else:
                        s = str(num)
                        if len(s) < 4:
                            s = '0' + s + '0'
                    if s == s[::-1]:
                        for i, j in zip(idx, s):
                            ret[i] = int(j)
                        cur = 0
                        s = ''.join(map(str, ret))
                        flag = 1
                        for i in range(n, 0, -3):
                            tmp = s[max(0, i-3):i]
                            if tmp:
                                cur += flag * int(tmp)
                            flag = -flag
                        if cur % 7 == 0:
                            return s
