class Solution {
public:


bool div7(const std::string& num) {
    long long val = 0;
    for (char c : num) {
        val = val * 10 + (c - '0');
        val %= 7;
    }
    return val == 0;
}


string largestPalindrome(int n, int k) {
        // 1,3,9: 9 n's
        // 2: 8999..8
        // 4: 88999988
        // 8: 888999888
        // 5: 599995
        // 6: 4: 8778
        // 7: 
        string res(n, '9');
        if(k==1 || k==3 || k==9){
            return res;
        }
        if(k==2){
            res[0] = res[n-1] = '8';
            return res;
        }
        if(k==4){
            res[0] = res[n-1] = '8';
            if(n>1){
                res[1] = res[n-2] = '8';
            }
            return res;
        }
        if(k==5){
            res[0] = res[n-1] = '5';
            return res;
        }
        if(k==8){
            res[0] = res[n-1] = '8';
            if(n>1){
                res[1] = res[n-2] = '8';
            }
            if(n>2){
                res[2] = res[n-3] = '8';
            }
            return res;
        }
        if(k==6){
            // odd = mid sum has to be congruent to 2 mod 3
            if(n==1 || n==2){
                return string(n, '6');
            }
            res[0] = res[n-1] = '8';
            int sum = (n-2) * 9;
            if(n%2==0){
                res[n/2] = res[n/2-1] = '7';
            }
            else{
                res[n/2] = '8';
            }
            return res;
        }
        if(k==7){
            res = string(n, '7');
            if(n==1 || n==2){
                return res;
            }
            res = string(n, '9');
                for(char c='9';c>='0';--c){
                    if(n%2==1){
                        res[n/2] = c;
                    }
                    else{
                        res[n/2] = res[n/2-1] = c;
                    }
                    if(div7(res)){
                        return res;
                    }
                }

            return string(n, '7');
        }

        return res;
    }
};
