class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s;
        for(int i=0;i<n;i++) s+='9';
        if(k==1){
            return s;
        }
        else if(k==2){
            s[0]='8';
            s[n-1]='8';
            return s;
        }
        else if(k==3){
            return s;
        }
        else if(k==4){
            s[0]='8';
            if(n>1) s[1]='8';
            s[n-1]='8';
            if(n>1) s[n-2]='8';
            return s;
        }
        else if(k==5){
            s[0]='5';
            s[n-1]='5';
            return s;
        }
        else if(k==6){
            if(n<6){
                if(n==1) s[0]='6';
                else if(n==2){
                    s[0]='6';
                    s[1]='6';
                }
                else if(n==3){
                    s[0]='8';
                    s[1]='8';
                    s[2]='8';
                }
                else if(n==4){
                    s[0]='8';
                    s[1]='7';
                    s[2]='7';
                    s[3]='8';
                }
                else {
                    s[0]='8';
                    s[2]='8';
                    s[4]='8';
                }
            }
            else{
                if(n%2==0){
                    s[0]='8';
                    s[1]='9';
                    s[n/2]='7';
                    s[n/2 -1]='7';
                    s[n-1]='8';
                    s[n-2]='9';
                }
                else{
                    s[0]='8';
                    s[n-1]='8';
                    s[n/2]='8';
                }
            }
            return s;
        }
        else if(k==7){
            if(n==1) s[0]='7';
            else if(n==2){
                s[0]='7';
                s[1]='7';
            }
            else{
                if(n%2==0){
                    for(int j=9;j>=1;j--){
                        s[n/2]='0'+j;
                        s[(n-1)/2]='0'+j;
                        int rem=0;
                        for(int i=0;i<n;i++){
                            int curr=s[i]-'0';
                            curr+=(rem*10);
                            rem=curr%7;
                        }
                        if(rem==0) return s;
                    }
                }
                else{
                    for(int j=9;j>=1;j--){
                        s[n/2]='0'+j;
                        int rem=0;
                        for(int i=0;i<n;i++){
                            int curr=s[i]-'0';
                            curr+=(rem*10);
                            rem=curr%7;
                        }
                        if(rem==0) return s;
                    }
                }
            }
            return s;
        }
        else if(k==8){
            s[0]='8';
            s[n-1]='8';
            if(n>1){
                s[1]='8';
                s[n-2]='8';
            }
            if(n>2){
                s[2]='8';
                s[n-3]='8';
            }
            return s;
        }
        return s;
    }
};
