class Solution {
    bool check(string s){
        int i = s.length()-1;
        while(i>=2){
            int x = s[i]-'0'; x*=2;
            int u = i-2; 
            string sx = s.substr(u,2);
            int ux = stoi(sx);
            // cout<<ux<<" "<<x<<"="<<ux-x<<endl;
            ux-=x;
            string d = "0";
            if(ux>=10) d = to_string(ux);
            else d+=to_string(ux);
            s[i-1]=d[1];
            s[i-2]=d[0];
            // cout<<s.substr(u,2)<<endl;
            i--;
        }
        int u = stoi(s.substr(0,2));
        return u%7==0;
    }
public:
    string largestPalindrome(int n, int k) {
        string ans = "";
        if(n==1){
            if(k==2 || k==4 || k==8) return "8";
            if(k==5) return "5";
            if(k==6) return "6";
            if(k==7) return "7";
        }
        if(k==1 || k==3 || k==9){
            while(n--) ans+='9';
            return ans;
        }else if(k==2){
            ans+='8'; n--;
            while(n>1) {ans+='9';n--;}
            if(n) ans+='8';
            return ans;
        }else if(k==5){
            ans+='5'; n--;
            while(n>1) {ans+='9';n--;}
            if(n) ans+='5';
            return ans;
        }else if(k==8){
            if(n<=6){
                while(n--) ans+='8';
                return ans;
            }
            ans+="888"; n-=6;
            while(n--) ans+="9";
            ans+="888";
        }else if(k==6){
            if(n==2) return "66";
            if(n%2){
                ans+="8";
                for(int i=1;i<(n/2);i++) ans+="9";
                ans+="8";
                for(int i=(n/2)+1;i<(n-1);i++) ans+="9";
                ans+="8";
            }else{
                ans+="8";
                for(int i=1;i<(n/2-1);i++) ans+="9";
                ans+="77";
                for(int i=(n/2)+1;i<(n-1);i++) ans+="9";
                ans+="8";
            }
            return ans;
        }else if(k==4){
            if(n==2) return "88";
            if(n==3) return "888";
            if(n==4) return "8888";
            ans+="88";
            n-=4;
            while(n--) ans+="9";
            ans+="88";
        }else if(k==7){
            if(n==2) return "77";
            if(n==3) return "959";
            if(n%2){
                int u=9;
                while(u>=0){
                    ans="";
                    for(int i=0;i<(n/2);i++) ans+="9";
                    char c = '0'+u;
                    ans+=c;
                    for(int i=(n/2)+1;i<n;i++) ans+="9";
                    // cout<<ans<<endl;
                    if(check(ans)) return ans;
                    u--;
                }
            }else{
                int u=9;
                while(u>=0){
                    ans="";
                    for(int i=0;i<(n/2-1);i++) ans+="9";
                    char c = '0'+u;
                    ans+=c;
                    ans+=c;
                    for(int i=(n/2)+1;i<(n);i++) ans+="9";
                    // cout<<ans<<"*"<<endl;
                    if(check(ans)) return ans;
                    u--;
                }
            }
        }
        return ans;
    }
};
