class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n==1:
            for i in range(9,0,-1):
                if str(i)==str(i)[::-1] and i%k==0:
                    return str(i)
        if n==2:
            for i in range(99,9,-1):
                if str(i)==str(i)[::-1] and i%k==0:
                    return str(i)
        if n==3:
            for i in range(999,99,-1):
                if str(i)==str(i)[::-1] and i%k==0:
                    return str(i)
        if n==4:
            for i in range(9999,999,-1):
                if str(i)==str(i)[::-1] and i%k==0:
                    return str(i)
        s=["9"]*n
        if k in [1,9]:
            pass
        elif k==2:
            s[0]=s[-1]="8"
        elif k==4:
            s[0]=s[1]=s[-1]=s[-2]="8"
        elif k==5:
            s[0]=s[-1]="5"
        elif k==6:
            s[0]=s[-1]="8"
            if n%2==0:
                md1=0+n//2
                md2=n-1-n//2
                s[md1]=s[md2]="7"
            else:
                s[n//2]="8"
        elif k==8:
            s[0]=s[1]=s[2]=s[-1]=s[-2]=s[-3]="8"
        else:
            md1=0+n//2
            md2=n-1-n//2
            for i in range(9,0,-1):
                s[md1]=s[md2]=str(i)
                t=0
                for i in s:
                    t=(10*t+int(i))%k
                if t==0:
                    break
        return "".join(s)
