class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans(n, '9');

        if (k == 1 || k == 3 || k==9) return ans;
        if (k == 5) {
            ans[0] = '5';
            ans.back() = '5';
            return ans;
        }
        if (k == 2) {
            ans[0] = '8';
            ans.back() = '8';
            return ans;            
        }
        if (k == 4) {
            ans[0] = '8';
            if (n == 1) return ans;
            ans[1] = '8';
            ans.back() = '8';
            ans[n-2] = '8';
            return ans;
        }
        if (k == 8) {
            ans[0] = '8';
            if (n == 1) return ans;
            ans[1] = '8';
            if (n == 2) return ans;
            ans[2] = '8';
            ans.back() = '8';
            ans[n-2] = '8';
            ans[n-3] = '8';
            return ans;
        }
        if (k == 6) {
            int m = n / 2;
            if (n == 1) ans = "6";
            else if (n == 2) ans = "66";
            else if ((n & 1) == 1) {
                ans[0] = '8';
                ans.back() = '8';
                ans[m] = '8';
            }
            else {
                ans[0] = '8';
                ans.back() = '8';
                ans[m] = '7';
                ans[m-1] = '7';
            }
            return ans;
        }

        if (k == 7) {
            int m = n / 2;
            int rn = 1, rm = 1;
            for (int i=(n % 6); i>=1; --i) rn = rn * 3 % 7;
            for (int i=(m % 6); i>=1; --i) rm = rm * 3 % 7;

            if ((n & 1) == 1) {
                for (char j=9; j>=0; --j) {
                    if ((rn-1-j*rm) % 7 == 0) ans[m] = '9' - j;
                }
            }
            else {
                for (char j=9; j>=0; --j) {
                    if ((rn-1-j*rm*6) % 7 == 0) {
                        ans[m] = '9' - j;
                        ans[m-1] = '9' - j;
                    }
                }
            }
            return ans;
        }
        return ans;
    }
};
