class Solution {
    public String largestPalindrome(int n, int k) {
        if (k == 1) {
            return "9".repeat(n);
        } else if (k == 2) {
            if (n == 1) {
                return "8";
            }
            return "8" + "9".repeat(n - 2) + "8";
        } else if (k == 3) {
            return "9".repeat(n);
        } else if (k == 4) {
            if (n < 4) {
                return "8".repeat(n);
            }
            return "88" + "9".repeat(n - 4) + "88";
        } else if (k == 5) {
            if (n == 1) {
                return "5";
            }
            if (n == 2) {
                return "55";
            }
            return "5" + "9".repeat((n - 2)) + "5";
        } else if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            if (n == 3) {
                return "888";
            }
            if (n % 2 == 0) {
                int nNine = (n - 4) / 2;
                return "8" + "9".repeat(nNine) + "77" + "9".repeat(nNine) + "8";
            } else {
                int nNine = (n - 3) / 2;
                return "8" + "9".repeat(nNine) + "8" + "9".repeat(nNine) + "8";
            }

        } else if (k == 7) {
            if (n % 2 == 1) {
                int nNine = (n - 1) / 2;
                if (nNine % 6 == 0) {
                    return "9".repeat(nNine) + "7" + "9".repeat(nNine);
                } else if (nNine % 6 == 1) {
                    return "9".repeat(nNine) + "5" + "9".repeat(nNine);
                } else if (nNine % 6 == 2) {
                    return "9".repeat(nNine) + "7" + "9".repeat(nNine);
                } else if (nNine % 6 == 3) {
                    return "9".repeat(nNine) + "4" + "9".repeat(nNine);
                } else if (nNine % 6 == 4) {
                    return "9".repeat(nNine) + "6" + "9".repeat(nNine);
                } else {
                    return "9".repeat(nNine) + "4" + "9".repeat(nNine);
                }
            } else {
                int nNine = (n - 1) / 2;
                if (nNine % 6 == 0) {
                    return "9".repeat(nNine) + "77" + "9".repeat(nNine);
                } else if (nNine % 6 == 1) {
                    return "9".repeat(nNine) + "77" + "9".repeat(nNine);
                } else if (nNine % 6 == 2) {
                    return "9".repeat(nNine) + "99" + "9".repeat(nNine);
                } else if (nNine % 6 == 3) {
                    return "9".repeat(nNine) + "44" + "9".repeat(nNine);
                } else if (nNine % 6 == 4) {
                    return "9".repeat(nNine) + "44" + "9".repeat(nNine);
                } else {
                    return "9".repeat(nNine) + "99" + "9".repeat(nNine);
                }
            }
        } else if (k == 8) {
            if (n < 7) {
                return "8".repeat(n);
            } else {
                int nNine = (n  - 6);
                return "888" + "9".repeat(nNine) + "888";
            }
        } else if (k == 9) {
            return "9".repeat(n);
        }
        return "0";
    }

}
