class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1||k==3||k==9) return string(n,'9');
        if(k==2){
            string s(n, '9');
            s[0] = '8';
            s[n-1] = '8';
            return s;
        }
        if(k==5){
            string s(n, '9');
            s[0] = '5';
            s[n-1] = '5';
            return s;
        }
        if(k==4){
            if(n==1) return "8";
            else if(n==2) return "88";
            else{
                string s(n, '9');
                s[0] = '8';
                s[1]='8';
                s[n-2]='8';
                s[n-1] = '8';
                return s;
            }
        }
        if(k==8){
            if(n<=6) return string(n, '8');
            else{
                string s(n, '9');
                s[0] = '8';
                s[1]='8';                
                s[2]='8';
                s[n-3]='8';
                s[n-2]='8';
                s[n-1] = '8';
                //cout<<s;
                return s;
            }
        }
        if(k==6){
            if(n==1) return "6";
            else if(n==2) return "66";
            else{
                string s(n, '9');
                s[0] = '8';
                s[n-1] = '8';
                if(n%2) s[n/2]='8';
                else{
                    s[n/2]='7';
                    s[n/2-1]='7';
                }
                return s;
            }
        }
        if(n==1) return "7";
        else if(n==2) return "77";
        string s(n, '9');
        if(n%2){
            for(int i=0;i<10;++i){
                s[n/2]='9'-i;
                if(is7(s)) return s;
            }
        }else{
            for(int i=0;i<10;++i){
                s[n/2]='9'-i;
                s[n/2-1]='9'-i;
                if(is7(s)) return s;
            }
        }
        return "";
    }
    bool is7(const std::string& s) {
        long long remainder = 0;
        for (char c : s) {
            // 将当前位添加到余数中
            remainder = (remainder * 10 + (c - '0')) % 7;
        }
        return remainder == 0;
    }
};
