class Solution {
    public String largestPalindrome(int n, int k) {
        char[] ans = new char[n];
        if(k == 1 || k == 3 || k == 9){
            Arrays.fill(ans,'9');
        }else if(k == 2 || k == 5){
            if (k == 2) k = 8;
            Arrays.fill(ans,'9');
            ans[0] = (char) ('0'+k);
            ans[n - 1] = (char) ('0'+k);
        }else if(k == 6){
            if(n<3){
                Arrays.fill(ans,'6');
            }else{
                Arrays.fill(ans,'9');
                ans[0] = '8';
                ans[n - 1] = '8';
                if((n & 1) == 1) ans[n / 2] = '8';
                else {
                    ans[n / 2] = '7';
                    ans[n / 2 - 1] = '7';
                }
            }
        }else if(k == 4){
            if(n <= 4)  Arrays.fill(ans,'8');
            else{
                Arrays.fill(ans,'9');
                ans[0] = '8';
                ans[n - 1] = '8';
                ans[1] = '8';
                ans[n - 2] = '8';
            }
        }else if(k == 8){
            if(n <= 6)  Arrays.fill(ans,'8');
            else{
                Arrays.fill(ans,'9');
                ans[0] = ans[1] = ans[2] ='8';
                ans[n - 1] = ans[n - 2] = ans[n - 3] ='8';
            }
        }else{
            Arrays.fill(ans,'9');
            int temp = n % 12;
            if(temp == 1 || temp == 2 || temp == 4 || temp == 5)change(ans,'7');
            else if (temp == 7 || temp == 8 || temp == 10 || temp == 11) change(ans,'4');
            else if (temp == 3) change(ans,'5');
            else if (temp == 9) change(ans,'6');

        }

        return new String(ans);
    }
    void change(char[] ans,char x){
        if((ans.length & 1) == 1) ans[ans.length / 2] = x;
        else{
            ans[ans.length / 2] = ans[ans.length / 2 - 1] = x;
        }
    }
}
