#define ll long long
class Solution {
    std::string subtract_one(const std::string& num) {
    std::string result = num;
    int i = result.length() - 1;
    while (i >= 0 && result[i] == '0') {
        result[i] = '9';
        i--;
    }
    if (i >= 0) {
        result[i]--;
    }
    return result;
}

bool is_divisible_by_7(const std::string& num) {
    long long remainder = 0;
    for (char digit : num) {
        remainder = (remainder * 10 + (digit - '0')) % 7;
    }
    return remainder == 0;
}

std::string largest_palindrome_divisible_by_7(int n) {
    std::string left;
    if (n % 2 == 0) {
        left = std::string(n / 2, '9');
    } else {
        left = std::string(n / 2 + 1, '9');
    }

    while (!left.empty() && left[0] != '0') {
        std::string right = left;
        std::reverse(right.begin(), right.end());

        std::string palindrome;
        if (n % 2 == 0) {
            palindrome = left + right;
        } else {
            palindrome = left + right.substr(1);
        }

        if (is_divisible_by_7(palindrome)) {
            return palindrome;
        }

        left = subtract_one(left);
    }

    return "No palindrome found";
}
