class Solution {
public:
    string largestPalindrome(int n, int k) {
        switch (k) {
        case 1:
        case 3:
        case 9:
            return build(n, "9");
        case 5: {
            if (n <= 2) return build(n, "5");
            return "5" + build(n-2, "9") + "5";
        }
        case 7: {
            if (n <= 2) return build(n, "7");
            string part = build((n-1)/2, "9");
            for (int i = 9; i >= 3; i--) {
                string whole = part + build(2 - n % 2, to_string(i)) + part;
                if (0 == devide(whole, 7)) return whole;
            }
            return "bad";
        }
        case 2: {
            if (n <= 2) return build(n, "8");
            return "8" + build(n-2, "9") + "8";
        }
        case 4: {
            if (n <= 4) return build(n, "8");
            return "88" + build(n-4, "9") + "88";
        }
        case 6: {
            if (n <= 2) return build(n, "6");
            if (n == 3) return "888";
            if (n == 4) return "8778";
            if (n % 2 == 1) {
                return "8" + build(n/2-1, "9") + "8" + build(n/2-1, "9") + "8";
            }
            return "8" + build(n/2-3, "9") + "9779" + build(n/2-3, "9") + "8";
        }
        case 8: {
            if (n <= 6) return build(n, "8");
            return "888" + build(n-6, "9") + "888";
        }

        } 
        return "";
    }
private:
    string build(int n, string c) {
        stringstream ss;
        for (int i = 0; i < n; i++) {
            ss << c;
        }
        return ss.str();
    }
    int devide(string num, int d) {
        int n = num[0] - '0';
        int i = 1;
        while (i < num.size()) {
            n %= d;
            n *= 10;
            n += num[i++] - '0';
        }
        n %= d;
        return n;
    }
};
