class Solution {
public:
    string largestPalindrome(int n, int k) {
        switch(k) {
            case 1:
            case 3:
            case 9:
             return string(n, '9');
            case 5:
                if (n == 1) {
                    return "5";
                }
             return "5" + string(n-2, '9') + "5";
            case 6:{
                if (n == 1) {
                    return "6";
                }
                if (n == 2) {
                    return "66";
                }
                if (n%2)
                return "8" + string((n-3)/2, '9') + "8" + string((n-3)/2, '9') + "8" ;
                return "8" + string(n/2-2, '9') + "77" + string(n/2-2, '9') + "8" ;
            }
            case 2:
                if (n == 1) {
                    return "8";
                }
              return "8" + string(n-2, '9') + "8";
            case 4:
                if (n < 4) {
                    return string(n, '8');
                }
              return "88" + string(n-4, '9') + "88";
            case 8:
                if (n < 6) {
                    return string(n, '8');
                }
              return "888" + string(n-6, '9') + "888";
        }
        int mo = 0;
        for(int cur = 9, i = 0;i<n; ++i, cur*=10){
            cur = cur%7;
            mo += cur;
            mo%=7;
        }
        if(mo == 0) return string(n, '9');
        vector<int> rec(10), red(10);
        // if(n%2 == 0) for(int i = 1; i<10; ++i) rec[i] = i*11;
        // else 
        for(int i = 1; i<10; ++i) rec[i] = i%7;
        for(int cur = 9, i = 1, nn = (n+1)/2; i<nn; ++i){
            for(int a = 1; a<10; ++a) {
                rec[a] = (rec[a]*10)%7;
            }
        }
        if(n%2 == 0) {
            for(int a = 1; a<10; ++a) {
               rec[a] = ((rec[a]*10)%7 + rec[a])%7;
            }
        }
        int v = 1;
        for(;v<10; ++v) {
            if(rec[v] == mo) break;
        }
        string ans =  string(n, '9');
        ans[n/2] -= v;
        if(n%2==0)  ans[n/2 - 1] -= v;
        return ans;
    }
};
