    //generate a number with n digits which is divisible by 1<=k<=9 and is palindrome
    // it has to be largest as well

    // for 1 u can return all 9s
    // for 2 u can return 8 + 99999 + 8
    // for 3 u can return 999999
    // for 4 u can return 97 + 99999 + 79
    // for 5 u can return 5 + 99999 + 5
    // for 6 u can return 8 + 99...rem rem ..9999 + 8
    // for 7 if even 999xxx999 type, if odd 999x999 type
    // for 8 u can return 996 ... 699
    // for 9 return 9999999999
    bool check(vector<int>&a){
        for(int i = 0; i < a.size()/2; ++i){
        if(a[i] != a[a.size() - i - 1])
            return false;
        }
        return true;
    }
    string largestPalindrome(int n, int k) {
        if(k == 1 || k == 3 || k == 9){
            string ans = "";
            for(int i = 0; i < n; ++i){
                ans += '9';
            }
            return ans;
        }
        if(n <= 5){
            vector<int> digs;
            for(int i = (int)pow(10, n) - 1; i >= k ; i--){
                if(i % k) continue;
                digs.clear();
                int x = i;
                while(x){
                    digs.push_back(x % 10);
                    x /= 10;
                }
                if(check(digs)){
                   return to_string(i);
                }
            }
        }

        string ans ="";
        if(k == 2 || k == 5){
            if(k == 2) ans += '8';
            else ans += '5';
            for(int i = 1; i <= n - 2; ++i){
                ans += '9';
            }
            if(k == 2) ans += '8';
            else ans += '5';

            return ans;
        }
        if(k == 4){
            ans += "88";
            for(int i = 1; i <= n - 4; ++i){
                ans += '9';
            }
            ans += "88";

            return ans;
        }
        if(k == 6){
            ans += "8";
            for(int i = 1; i < (n-2 + 1)/2; ++i){
                ans += '9';
            }
            if(n & 1) ans += '8';
            else ans += "77";
            for(int i = 1; i < (n-2 + 1)/2; ++i){
                ans += '9';
            }
            ans += "8";
            return ans;
        }
        if(k == 8){
            ans += "888";
            for(int i = 1; i <= (n - 6); ++i){
                ans += '9';
            }
            ans += "888";

            return ans;
        }

        auto check = [&](string s){

            int rem = 0, p = 1;
            for(int i = s.size() - 1; i >= 0; --i){
                rem = (rem + (s[i] - '0')*p) % 7;
                p = (p*10) % 7;
            }
            if(rem == 0) return true;
            return false;
        };
        if(k == 7){
            string a = "", b = "";
            for(int i = 1; i < (n + 1)/2; ++i){
                a += "9";
                b += "9";
            }

            for(int i = 9; i >= 0; --i){
                char c = '0' + i;
                string mid = "";
                mid += c;
                if(n % 2 == 0) mid += mid;
                string net = a + mid + a;
                if(check(net)) return net;
            }
        }
        return "";
    }
};
