class Solution:
    def get1(self,n):
        return "9" * n
    def get2(self,n):
        if n == 1:
            return "8"
        if n == 2:
            return "88"
        return "8" + (n - 2) * "9" + "8"
    def get3(self,n):
        return "9" * n 

    def get4(self,n):
        if n == 1:
            return "8"
        if n == 2:
            return "88"
        if n == 3:
            return "888"
        if n == 4:
            return "8888"
        return "88" + (n - 4) * "9" + "88"
    def get5(self, n):
        if n == 1:
            return "5"
        if n == 2:
            return "55"
        return "5" + (n - 2) * "9" + "5"
    def get6(self,n):
        if n == 1:
            return "6"
        if n == 2:
            return "66"
        if n == 3:
            return "888"
        if n == 4:
            return "8778"
        if n % 2 == 0:
            return "8" + (n - 4) // 2 * "9" + "77" + (n - 4) // 2 * "9" + "8"
        else:
            return "8" + (n - 3) // 2 * "9" + "8" + (n - 3) // 2 * "9" + "8"
    def get7(self,n):
        if n == 1:
            return "7"
        if n == 2:
            return "77"
        if n == 3:
            return "959"
        if n == 4:
            return "9779"
        if n == 5:
            return "99799"
        if n % 2 == 0:
            zhongjian = ["7","9","4","4","9","7"]
            return (n - 2) // 2 * "9" + zhongjian[((n - 4) // 2) % len(zhongjian)] * 2  +  (n - 2) // 2 * "9"
        else:
            zhongjian = ["5","7","4","6","4","7"]
            return n // 2 * "9" + zhongjian[((n - 3) // 2) % len(zhongjian)] + n // 2 * "9"

    def get8(self,n):
        if n <= 6:
            return n * "8"
        return "888" + (n - 6) * "9" + "888"
    def get9(self,n):
        return "9" * n

    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return self.get1(n)
        if k == 2:
            return self.get2(n)
        if k == 3:
            return self.get3(n)
        if k == 4:
            return self.get4(n)
        if k == 5:
            return self.get5(n)
        if k == 6:
            return self.get6(n)
        if k == 7:
            return self.get7(n)
        if k == 8:
            return self.get8(n)
        if k == 9:
            return self.get9(n)
