class Solution:


    def largestPalindrome(self, n: int, k: int) -> str:

        def is_divisible_by_7(number_str):
            mod = 7
            current_mod = 0

            for digit in number_str:
                current_mod = (current_mod * 10 + int(digit)) % mod

            return current_mod == 0

        if k == 1 or k == 9 or k == 3:
            return '9'*n

        if k == 2:
            if n == 1:
                return '8'
            if n == 2:
                return '88'
            return '8' + '9'*(n-2) + '8'

        if k == 4:
            if n <= 4:
                return '8'*n
            return '88' + '9'*(n-4) + '88'

        if k == 5:
            if n == 1:
                return '5'
            if n == 2:
                return '55'

            return '5' + '9'*(n-2) + '5'

        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n == 3:
                return '888'

            if n%2 == 0:
                temp = n//2
                s = '8' + '9'*(temp-2) + '7'
                return s + s[::-1]

            if n%2 == 1:
                temp = n//2
                s = '8' + '9'*(temp-1)
                return s + '8' + s[::-1]

        if k == 7:
            d = {13: '9999997999999', 12: '999999999999', 11: '99999499999', 10: '9999449999', 9: '999969999', 8: '99944999', 7: '9994999', 6: '999999', 5: '99799', 4: '9779', 3: '959', 2: '77', 1: '7'}

            if n <= 13:
                return d[n]

            temp = n//2

            for i in range(9, -1, -1):

                if n%2 == 0:
                    s = '9'*(temp-1) + str(i)
                    s = s + s[::-1]
                    if is_divisible_by_7(s):
                        return s
                else:
                    s = '9'*temp + str(i) + '9'*temp
                    if is_divisible_by_7(s):
                        return s

            return '9'*n

        if k == 8:
            if n <= 6:
                return '8'*n
            return '888' + '9'*(n-6) + '888'


        return '9'*n
