class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (n == 1) {
            if (k >= 5) return to_string(k);
            if (k == 4) return "8";
            if (k == 3 || k == 1) return "9";
            if (k == 2) return "8";
        }
        if (k == 1) {
            return string(n, '9');
        } else if (k == 2) {
            return "8" + string(n - 2, '9') + "8";
        } else if (k == 3 || k == 9) {
            return string(n, '9');
        } else if (k == 4) {
            if (n >= 4) {
                return "88" + string(n - 4, '9') + "88";
            } else if (n == 3) {
                return "888";
            } else if (n == 2) {
                return "88";
            }
        } else if (k == 5) {
            return "5" + string(n - 2, '9') + "5";
        } else if (k == 6) {
            if (n == 2) return "66";
            if ((n - 2) % 2 != 0) {
                return "8" + string((n - 2) / 2, '9') + "8" + string((n - 2) / 2, '9') + "8";
            }
            if (n == 4) return "8778";
            if (n >= 6) {
                return "8" + string((n - 2) / 2 - 1, '9') + "77" + string((n - 2) / 2 - 1, '9') + "8";
            }
        } else if (k == 7) {
            int cnt = 0, mid = (n / 2) % 6;
            vector<int> arr = {1, 3, 2, -1, -3, -2};
            for (int i = 0, idx = 0; i < n; i++) {
                cnt += arr[idx] * 9;
                idx++;
                idx %= 6;
            }
            // if (n % 2 == 0) cout << n << ' ' << cnt % 7 << ' ' << arr[mid] << ' ' << arr[mid] << '\n';
            if (n == 2) return "77";
            if (n == 3) return "959";
            if (n == 4) return "9779";
            if (n == 5) return "99799";
            if (n % 6 == 0) return string(n, '9');
            if (n % 2 != 0) {
                cnt -= arr[mid] * 9;
                for (int i = 8; i >= 0; i--) {
                    if ((cnt + arr[mid] * i) % 7 == 0) {
                        return string(n / 2, '9') + to_string(i) + string(n / 2, '9'); 
                    }
                }
                cout << n << ' ' << cnt % 7 << ' ' << arr[mid] << '\n';
            } else if (n % 2 == 0) {
                int second = n / 2, first = mid - 1;
                cnt -= arr[first % 6] * 9;
                cnt -= arr[second % 6] * 9;
                for (int i = 8; i >= 0; i--) {
                    if ((cnt + arr[first % 6] * i + arr[second % 6] * i) % 7 == 0) {
                        return string(n / 2 - 1, '9') + to_string(i) + to_string(i) + string(n / 2 - 1, '9'); 
                    }
                }

            }
            if (n == 7) return "9994999";
            if (n == 8) return "99944999";
            if (n == 9) return "999969999";
            if (n == 10) return "9999449999";
            if (n == 11) return "99999499999";
            if (n == 13) return "9999997999999";
            if (n == 14) return "99999977999999";
            if (cnt % 7 == 0) return string(n, '9');
        } else if (k == 8) {
            if (n <= 5) return string(n, '8');
            if (n >= 6) return "888" + string(n - 6, '9') + "888";
        }
        return "0";
    }
};
