class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ans(n, '9');
        auto POW = [&](long long a, long long b, int mod) {
            long long res = 1;
            while (b > 0) {
                if (b % 2 == 1) {   
                    res = (res * a) % mod;
                }
                a = (a * a) % mod;   
                b /= 2;              
            }
            return res;
        };
        if(k==1) return ans;
        else if(k==2){
           ans.front()='8';
           ans.back()='8';
           return ans;
        }else if(k==3){
           return ans;
        }else if(k==4){
            for(int i=0;i<2;i++){
                if(i<n){
                    ans[i]='8';
                }
                if(n-i-1>=0){
                    ans[n-i-1]='8';
                }
            }
            return ans;
        }else if(k==5){
            ans.front()='5';
            ans.back()='5';
            return ans;
        }
        else if(k==6){
            if(n==1)return "6";
            if(n==2)return "66";
            if(n%2==0){
               string s;
               s.push_back('8');
               for(int i=0;i<n/2-2;i++){
                s.push_back('9');
               }
               s.push_back('7');
               string rev=s;
               reverse(rev.begin(), rev.end());
               return s+rev;
            }else{
               ans.back()='8';
               ans.front()='8';
               ans[n/2]='8';
            }
        }else if(k==7){
           if(n==1)return "7";
           if(n==2)return "77";
           if(n&1){
              long long val=0;
              long long pw=1;
              for(int i=0;i<n;i++){
                if(i==n/2){
                    pw*=10;
                    pw%=7;
                    continue;
                }
                long long v=pw*9;
                v%=7;
                val+=v;
                val%=7;
                pw*=10;
                pw%=7; 
              }
              for(int i=9;i>=0;i--){
                int cur=POW(10, n/2, 7)*i;
                cur%=7;
                if((val+cur)%7==0){
                    ans[n/2]=i+'0';
                    break;
                }
              }
              return ans;
           }else{
                long long val=0;
                long long pw=1;
                for(int i=0;i<n;i++){
                    if(i==n/2){
                        pw*=10;
                        pw%=7;
                        continue;
                    }
                    if(i==(n/2)-1){
                        pw*=10;
                        pw%=7;
                        continue;
                    }
                    long long v=pw*9;
                    v%=7;
                    val+=v;
                    val%=7;
                    pw*=10;
                    pw%=7; 
                }
                for(int i=9;i>=0;i--){
                    long long  v1=POW(10, n/2, 7)*i;
                    v1%=7;
                    long long v2=POW(10, n/2-1, 7)*i;
                    v2%=7;
                    if((val+v1+v2)%7==0){
                        ans[n/2]=i+'0';
                        ans[n/2-1]=i+'0';
                        break;
                    }
                }
                return ans;
           }
        }else if(k==8){
            ans.front()='8';
            ans.back()='8';
            if(n<=2) return ans;
            for(int i=0;i<3;i++){
                if(i<n){
                    ans[i]='8';
                }
                if(n-i-1>=0){
                    ans[n-i-1]='8';
                }
            }
            return ans;
        }
        else if(k==9){
           return ans;
        }
        return ans;
    }
};
