class Solution {
private:
    long long dpArray[100005][10];
    string maxPalindrome = "0";

    long long modExp(long long base, long long exp, long long mod) {
        base %= mod;
        long long result = 1;
        while (exp) {
            if (exp % 2) {
                result = result * base % mod;
                --exp;
            } else {
                base = base * base % mod;
                exp /= 2;
            }
        }
        return result;
    }

    bool searchPalindrome(int idx, int remainder, long long divisor, long long length, string &currentPalin) {
        long long mirrorIdx = (length - 1) - idx;
        if (mirrorIdx < idx) {
            if (remainder != 0) return false;
            if (currentPalin > maxPalindrome) {
                maxPalindrome = currentPalin;
            }
            return true;
        }

        if (dpArray[idx][remainder] != -1) return dpArray[idx][remainder];

        int startDigit = (idx == 0) ? 1 : 0;
        for (int digit = 9; digit >= startDigit; --digit) {
            long long powerAtIdx = modExp(10, idx, divisor);
            long long powerAtMirrorIdx = modExp(10, mirrorIdx, divisor);
            powerAtIdx = (powerAtIdx * digit) % divisor;
            powerAtMirrorIdx = (powerAtMirrorIdx * digit) % divisor;

            currentPalin[idx] = currentPalin[mirrorIdx] = char(digit + '0');
            if (idx != mirrorIdx) powerAtIdx = (powerAtIdx + powerAtMirrorIdx) % divisor;

            if (searchPalindrome(idx + 1, (remainder + powerAtIdx) % divisor, divisor, length, currentPalin)) {
                return dpArray[idx][remainder] = true;
            }
        }

        return dpArray[idx][remainder] = false;
    }

public:
    string largestPalindrome(int numDigits, int div) {
        memset(dpArray, -1, sizeof(dpArray));
        string palindrome(numDigits, '0');
        searchPalindrome(0, 0, div, numDigits, palindrome);
        return maxPalindrome;
    }
};
