class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def Judge(n):
            s = str(n)
            for i in range(len(s)//2):
                if s[i] != s[-i-1]:
                    return False
            return True

        if n <=6 :
            for i in range(pow(10,n),pow(10,n-1),-1):
                if i %k == 0 and Judge(i):
                    return str(i)
        elif k == 1 or k == 3 or k == 9:
            s = '9' * n
            return s
        elif k == 2:
            s = '9' * (n-2)
            s = '8' + s + '8'
            return s
        elif k == 5:
            s = '9' * (n-2)
            s = '5' + s +'5'
            return s
        elif k == 4:
            s = '9' * (n-4)
            s = '88' + s + '88'
            return s
        elif k == 8:
            s = '9' * (n -6)
            s = '888' + s + '888'
            return s
        elif k == 6:
            if n%2 == 1:
                s1 = '8' + ('9' * (n//2 - 1)) + '8' + ('9' *(n//2 - 1)) + '8'
                return s1
            else:
                s1 = '8' + ('9' * (n//2 - 2)) + '77' + ('9' *(n//2 - 2)) + '8'
                return s1
        else:
            mod0 = [1] * (n+1)
            for i in range(1,n+1):
                mod0[i] = (mod0[i-1] * 10) % 7
            if n%2 == 0:
                s1 = '9' * (n//2-1)
                rem = 0
                for i in range(0,n):
                    if i != n//2 and n//2 - 1 != i:
                        rem += mod0[i]*9
                        rem %= 7
                for i in range(9,1,-1):
                    if (i * (mod0[n//2] + mod0[n//2-1])+rem)%7 == 0:
                        s1 = s1 + chr(ord('0') + i)
                        s1 = s1 + chr(ord('0') + i)
                        break
                s1 = s1 + ('9' *(n//2-1))
                return s1
            else:
                s1 = '9' * (n//2)
                rem = 0
                for i in range(0,n):
                    if i != n//2:
                        rem += mod0[i]*9
                        rem %= 7
                for i in range(9,1,-1):
                    if (i * (mod0[n//2]) + rem)%7 == 0:
                        s1 = s1 + chr(ord('0') + i)
                        break
                s1 = s1 + ('9' *(n//2))
                return s1
