int mod = 7;
class Solution {
public:

    int pwr(int a,int n){
    int res =1;

    while(n){
        if(n&1){
            res = (res*1LL*a)%mod;
        }

        a = (a*1LL*a)%mod;

        n>>=1;
    }

    return res;

}
