import sys
sys.set_int_max_str_digits(1000000)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1 or k==3 or k==9:
            return '9'*n
        if k==5:
            if n<=2:
                return '5'*n
            else:
                return '5'+'9'*(n-2)+'5'
        if k==2:
            if n<=2:
                return '8'*n
            else:
                return '8'+'9'*(n-2)+'8'
        if k==4:
            if n<=4:
                return '8'*n
            else:
                return '8'*2+'9'*(n-4)+'8'*2
        if k==8:
            if n<=6:
                return '8'*n
            else:
                return '8'*3+'9'*(n-6)+'8'*3
        if k==6:
            if n<=2:
                return '6'*n
            else:
                # 长度为奇数
                if n%2:
                    return '8'+'9'*((n-1)//2-1)+'8'+'9'*((n-1)//2-1)+'8'
                # 长度为偶数
                else:
                    return '8'+'9'*((n-4)//2)+'7'+'7'+'9'*((n-4)//2)+'8'
        if k==7:
            if n<=2:
                return '7'*n
            else:
                # 99X99
                for i in range(9, -1, -1):
                    # 长度为奇数
                    if n%2:
                        num = '9'*(n//2)+str(i)+'9'*(n//2)
                        if not int(num)%7:
                            return num
                    # 长度为偶数
                    else:
                        num = '9'*(n//2-1)+str(i)*2+'9'*(n//2-1)
                        if not int(num)%7:
                            return num
