class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return '9' * n
        elif k == 2:
            if n == 1:
                return '8'
            elif n == 2:
                return '88'
            else:
                return '8' + '9' * (n-2) + '8'
        elif k == 3 or k == 9:
            return '9' * n
        elif k == 4:
            if n == 1:
                return '8'
            elif n == 2:
                return '88'
            elif n == 3:
                return '888'
            elif n == 4:
                return '8888'
            else:
                return '88' + '9' * (n-4) + '88'
        elif k == 5:
            if n == 1:
                return '5'
            elif n == 2:
                return '55'
            else:
                return '5' + '9' * (n-2) + '5'
        elif k == 6:
            if n == 1:
                return '6'
            elif n == 2:
                return '66'
            elif n == 3:
                return '888'
            elif n == 4:
                return '8778'
            elif n % 2 == 1:
                return '8' + ((n-3) // 2) * '9' + '8' + ((n-3) // 2) * '9' + '8'
            else:
                return '8' + ((n-4) // 2) * '9' + '77' + ((n-4) // 2) * '9' + '8'
        elif k == 8:
            if n == 1:
                return '8'
            elif n == 2:
                return '88'
            elif n == 3:
                return '888'
            elif n == 4:
                return '8888'
            elif n == 5:
                return '88888'
            elif n == 6:
                return '888888'
            else:
                return '888' + '9' * (n-6) + '888'
        elif k == 7:
            if n == 1:
                return '7'
            elif n == 2:
                return '77'
            elif n == 3:
                return '959'
            elif n == 4:
                return '9779'
            elif n == 5:
                return '99799'
            elif n == 6:
                return '999999'
            elif n % 6 == 0:
                return '9' * n
            else:
                table_9_rem = {1:2, 2:1, 3:5, 4:3, 5:4, 0:0}
                table_10_rem = {1:3, 2:2, 3:6, 4:4, 5:5, 0:1}
                table_double_rem = {33:5, 44:2, 55:6, 66:3, 77:0, 88:4, 99:1}
                check = [99, 88, 77, 66, 55, 44, 33]
                if n % 2 == 1:
                    beginnin = '9' * ((n-1)//2)
                    beginnin_rem = table_9_rem[len(beginnin) % 6]
                    tens = table_10_rem[(n - len(beginnin)) % 6]
                    total_rem = (beginnin_rem * tens + beginnin_rem) % 7
                    for i in range(9, -1, -1):
                        tens = table_10_rem[((n-1)//2) % 6]
                        total = (total_rem + i*tens) % 7
                        if total == 0:
                            return '9' * ((n-1)//2) + str(i) + '9' * ((n-1)//2)
                else:
                    beginnin = '9' * ((n-2)//2)
                    beginnin_rem = table_9_rem[len(beginnin) % 6]
                    tens = table_10_rem[(n - len(beginnin)) % 6]
                    total_rem = (beginnin_rem * tens + beginnin_rem) % 7
                    for i in check:
                        tens = table_10_rem[((n-2)//2) % 6]
                        total = (total_rem + tens * i) % 7
                        if total == 0:
                            return '9' * ((n-2)//2) + str(i) + '9' * ((n-2)//2)
