class Solution {
public:
    void subStr(string &a, int num) {
        int last = (a[a.length()-2] - '0') * 10 + a[a.length()-1] - '0';
        int diff = last - num;

        a.pop_back(); a.pop_back();
        if (diff < 10) {
            a += "0" + to_string(diff);
        } else {
            a += to_string(diff);
        }
    }

    bool isSevenDivis(string &str) {
        if (str.length() <= 8) {
            return stoi(str) % 7 == 0;
        }
        int last = str[str.length()-1] - '0';

        str.pop_back();
        subStr(str, last * 2);
        return isSevenDivis(str);
    }

    string largestPalindrome(int n, int k) {
        // largest palindrome with n digits that is divisible by k
        string ans = "";

        if (k == 1 || k == 3 || k == 9) {
            while (n--) {
                ans += '9';
            }
        } else if (k == 2) {
            ans = '8';
            if (n > 1) {
                while (n > 2) {
                    ans += '9';
                    n--;
                }
                ans += '8';
            }
        } else if (k == 4) {
            if (n <= 4) {
                while (n--) {
                    ans += "8";
                }
            } else {
                ans = "88";
                while (n > 4) {
                    ans += "9";
                    n--;
                }
                ans += "88";
            }
        } else if (k == 5) {
            ans = "5";
            if (n > 1) {
                while (n > 2) {
                    ans += "9";
                    n--;
                }
                ans += "5";
            }
        } else if (k == 6) {
            if (n == 1) {
                ans = "6";
            } else if (n == 2) {
                ans = "66";
            } else {
                if (n % 2 != 0) {
                    ans = "8";
                    int cnt = (n - 3) / 2;
                    while (cnt--) {
                        ans += '9';
                    }
                    ans += '8';
                    cnt = (n - 3) / 2;
                    while (cnt--) {
                        ans += '9';
                    }
                    ans += '8';
                } else {
                    ans = "8";
                    int cnt = (n - 4) / 2;
                    while (cnt--) {
                        ans += '9';
                    }
                    ans += "77";
                    cnt = (n - 4) / 2;
                    while (cnt--) {
                        ans += '9';
                    }
                    ans += '8';
                }
            }
        } else if (k == 7) {
            if (n == 1) {
                ans = "7";
            } else if (n == 2) {
                ans = "77";
            } else {
                if (n % 2 != 0) {
                    int cnt = (n - 1) / 2;
                    string acc = "";
                    while (cnt--) {
                        acc += '9';
                    }

                    for (int i = 9; i >= 0; i--) {
                        string str = acc + to_string(i) + acc;
                        if (isSevenDivis(str)) {
                            ans = acc + to_string(i) + acc;
                            break;
                        }
                    }
                } else {
                    int cnt = (n - 2) / 2;
                    string acc = "";
                    while (cnt--) {
                        acc += '9';
                    }

                    for (int i = 9; i >= 0; i--) {
                        string str = acc + to_string(i) + to_string(i) + acc;
                        if (isSevenDivis(str)) {
                            ans = acc + to_string(i) + to_string(i) + acc;
                            break;
                        }
                    }
                }
            }
        } else {
            if (n <= 6) {
                while (n--) {
                    ans += '8';
                }
            } else {
                ans = "888";
                while (n > 6) {
                    ans += '9';
                    n--;
                }
                ans += "888";
            }
        }

        return ans;
    }
};
