class Solution {
public:

    bool isDiv(string s,int num)
    {
        int cr=0;

        for(int i=0;i<s.length();i++){

            int n=s[i]-48;

            n=cr*10+n;

            if(n<num){
                if(i==s.length()-1) return false;
                i++;
                n=n*10+(s[i]-48);
            }

            cr=n%num;
        }

        return cr==0;
    }


    string largestPalindrome(int n, int k) {
        string ans;
        if(k==1){
            while(n--) ans.push_back('9');
        }
        else if(k==2){
            ans.push_back('8');
            if(n==1) return ans;
            n-=2;
            while(n--) ans.push_back('9');
            ans.push_back('8'); 
        }
        else if(k==3){
            while(n--) ans.push_back('9');
        }
        else if(k==4){
            if(n<5){
                while(n--) ans.push_back('8');
                return ans;
            }

            ans="88";
            n-=4;
            while(n--) ans.push_back('9');
            ans+="88";
        }
        else if(k==5){
            if(n==1) return "5";
            n-=2;
            ans.push_back('5');
            while(n--) ans.push_back('9');

            ans.push_back('5');
        }
        else if(k==6){
            if(n==1) return "6";
            if(n==2) return "66";
            n-=2;
            ans.push_back('8');
            bool ev=0;
            if(n%2==0) ev=1;
            n--;
            n/=2;

            while(n--) ans.push_back('9');

            string tmp=ans;
            reverse(tmp.begin(),tmp.end());

            if(ev){
                ans+="77";
            }
            else ans+="8";

            ans+=tmp;
        }
        else if(k==7){
            if(n==1) return "7";
            if(n==2) return "77";

            bool isEv=0;
            if(n%2==0) isEv=1;

            n--;
            n/=2;

            while(n--) ans.push_back('9');

            string tmp=ans;
            reverse(tmp.begin(),tmp.end());

            for(int i=9;i>=0;i--){
                string mid=to_string(i);

                if(isEv)
                    mid+=to_string(i);

                if(isDiv(ans+mid+tmp,k))
                    return ans+mid+tmp;

            }

        }
        else if(k==8){
            if(n<7){
                while(n--) ans.push_back('8');
                return ans;
            }
            ans="888";
            n-=6;
            while(n--) ans.push_back('9');
            ans+="888";
        }
        else{
            while(n--) ans.push_back('9');
            return ans;
        }

        return ans;
    }
};
