class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (n == 1) {
            string ans[9] = {"9", "8", "9", "8", "5", "6", "7", "8", "9"};
            return ans[k - 1];
        }
        if (k == 1 || k == 3 || k == 9) return string(n, '9');
        if (k == 2) return "8" + string(n - 2, '9') + "8";
        if (k == 4) return (n <= 4)? string(n, '8'): "88" + string(n - 4, '9') + "88";
        if (k == 5) return "5" + string(n - 2, '9') + "5";
        if (k == 8) return (n <= 6)? string(n, '8'): "888" + string(n - 6, '9') + "888";
        if (k == 6) {
            if (n == 2) return "66";
            if (n == 3) return "888";
            if (n == 4) return "8778";
            if (n & 1) return "89" + string((n - 5) >> 1, '9') + "8" + string((n - 5) >> 1, '9') + "98";
            return "89" + string((n - 6) >> 1, '9') + "77" + string((n - 6) >> 1, '9') + "98";
        }
        string s = string((n + 1) >> 1, '9');
        while (true) {
            string t = (n & 1)? s.substr(0, s.size() - 1): s;
            reverse(t.begin(), t.end());
            t = s + t;
            sub(s);
            if (div7(t)) return t;
        }
    }

    bool div7(string& s) {
        int i, k = 0, n = s.size();
        for (i = 0; i < n; i++) {
            k = k * 10 + (s[i] - '0');
            k %= 7;
        }
        return k == 0;
    }

    void sub(string& s) {
        int n = s.size();
        s[--n]--;
        while (n > 0 && s[n] < 0) s[n] = '9', s[n - 1]--;
    }
};
