function largestPalindrome(n: number, k: number): string {
    if (n < 7) {
        for (let i = (10 ** n) - 1; i >= 0; i--) {
            if (i.toString().split('').reverse().join('') !== i.toString()) {
                continue;
            }

            if (i % k === 0) {
                return i.toString();
            }
        }
    }

    if (k === 1 || k === 3 || k === 9) {
        return '9'.repeat(n);
    } else if (k === 2) {
        return `8${'9'.repeat(n - 2)}8`;
    } else if (k === 4) {
        return `88${'9'.repeat(n - 4)}88`;
    } else if (k === 5) {
        return `5${'9'.repeat(n - 2)}5`;
    } else if (k === 6) {
        if (n % 2) { // нечетное
            return `8${'9'.repeat((n - 3) / 2)}8${'9'.repeat((n - 3) / 2)}8`;
        } else {
            return `8${'9'.repeat((n - 4) / 2)}77${'9'.repeat((n - 4) / 2)}8`;
        }
    } else if (k === 7) {
        for (let j = 9; j >= 0; j--) {
            let jj;
            if (n % 2) { // нечетное
                jj = `${'9'.repeat((n - 1) / 2)}${j}${'9'.repeat((n - 1) / 2)}`;
            } else {
                jj = `${'9'.repeat((n - 2) / 2)}${j}${j}${'9'.repeat((n - 2) / 2)}`;
            }

            if (check7(jj)) {
                return jj;
            }
        }
    } else if (k === 8) {
        return `888${'9'.repeat(n - 6)}888`;
    }
};

function check7(num: string): boolean {
    return num.split('').reduce((acc, i) => (acc * 3 + +i) % 7, 0) % 7 === 0;
}
