class Solution {
public:
    string largestPalindrome(int n, int k) {

        if (k==1 || k==3 || k==9) {
            return string(n, '9');
        }

        if (k==2) {
            string temp(n, '9');
            temp[0]='8';
            temp.back() = '8';
            return temp;
        }

        if (k==5) {
            string temp(n, '9');
            temp[0]='5';
            temp.back()='5';
            return temp;
        }

        if (k==4) {
            string temp(n, '9');
            for (int i=0, j=n-1; i<2 && i<n; ++i, --j) {
                temp[i]='8';
                temp[j]='8';
            }
            return temp;
        }

        if (k==8) {
            string temp(n, '9');
            for (int i=0, j=n-1; i<3 && i<n; ++i, --j) {
                temp[i]='8';
                temp[j]='8';
            }
            return temp;
        }

        if (k==6) {
            if (n<=2) {
                return string(n, '6');
            }
            string temp(n, '9');
            temp[0]='8';
            temp.back()='8';
            int temp2=(n-2) * 9 +16;
            if (temp2%3) {
                int temp3=(temp2%3);
                if (n & 1) {
                    temp[n/2] -= temp3;
                } else {
                    if (temp3 == 2) {
                        temp[n/2]--;
                        temp[n/2 - 1]--;
                    } else {
                        temp[n/2]-=2;
                        temp[n/2 - 1]-=2;
                    }
                }
            }
            return temp;
        }

        if (k==7) {
            string temp(n, '9');
            find(temp, 0, n-1);
            return temp;
        }
        return "";
    }

    bool find(string& temp, int i, int j) {
        if (i>j) {
            string temp2=temp;

            for (int i=0; i<temp2.size(); ++i) {
                int temp3 = (temp2[i] - '0');
                if (i) temp3 += (temp2[i - 1] - '0') * 10;
                int temp4 = ((temp3/7) * 7) % 10;
                if (temp2[i] >= '0'+temp4) {
                    temp2[i] -= temp4;
                } else {
                    temp2[i] -= temp4;
                    temp2[i] += 10;
                }
            }
            return temp2.back()=='0';
        }

        for (int k=9; k>=0; --k) {
            temp[i]='0'+k;
            temp[j]='0'+k;
            if (find(temp, i+1, j-1)) return true;
        }
        return false;
    }
};
