class Solution {
public:
    string largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9)
            return string(n,'9');
        else if(k==2){
            if(n<=2)
                return string(n,'8');
            else
                return "8"+string(n-2,'9')+"8";
        }
        else if(k==4){
            if(n<=4)
                return string(n,'8');
            else
                return "88"+string(n-4,'9')+"88";
        }
        else if(k==8){
            if(n<=6)
                return string(n,'8');
            else
                return "888"+string(n-6,'9')+"888";
        }
        else if(k==5){
            if(n==1)
                return "5";
            else if(n==2)
                return "55";
            else
                return "5"+string(n-2,'9')+"5";
        }
        else if (k == 6) {
            if (n == 1)
                return "6";
            else if (n == 2)
                return "66";
            else if (n == 3)
                return "888";
            else if (n == 4)
                return "8778";
            else{
                int x = (n-1)/2;
                string ans = string(x,'9');
                ans[0]='8';
                string suff = ans;
                reverse(suff.begin(),suff.end());
                if((n-2*x)%2==0)
                    return ans+"77"+suff;
                else
                    return ans+"8"+suff;
            }
        } 
        else if(k==7){
            if (n == 1)
                return "7";
            else if (n == 2)
                return "77";
            else if (n == 3)
                return "959";
            else if (n == 4)
                return "9779";
            else{
                int x = (n-1)/2;
                string ans = string(x,'9');
                string suff = ans;
                for(char c='9';c>='0';c--){
                    string temp = ans + string(n-2*x,c) + suff;
                    if(isDivisibleByK(temp,k))
                        return temp;
                }
            }
        }
        return string(n,'9');
    }

    bool isDivisibleByK(string& numStr, int k) {
        long long num = 0;
        for (char c : numStr) {
            num = (num * 10 + (c - '0')) % k;
        }
        return num == 0;
    }
};
