D = 10
debug = 0
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3:
            return "9"*n
        if k == 2:
            return "8" if n==1 else ("8"+("9"*(n-2)+"8"))
        if n == 1:
            for j in range(D-1,k-1,-1):
                if not j%k:
                    return str(j)
            return "k"
        if k == 5:
            return "5" if n == 1 else ("5"+("9"*(n-2))+"5")
        if k == 4:
            if n<5:
                return "8"*n
            return "88" + ("9"*(n-4)) + "88"
        lefts = []
        for i in range(n):
            lefts.append([(lefts[-1][j]*10)%k if i else j%k for j in range(D)])
        if debug:
            print(lefts)
        i = n-1
        last = [-1] * k
        alls = []
        addorder = []
        map0 = {}
        while i>=n//2:
            now = [-1] * k
            noworder = []
            ri = n-i-1
            if i == n-1:
                for j in range(D-1,0,-1):
                    left = (lefts[i][j]+lefts[ri][j])%k
                    if now[left] == -1:
                        now[left] = j
                        noworder.append(left)
            else:
                for pj in addorder:
                    for j in range(D-1,-1,-1):
                        left = (pj+lefts[i][j]+lefts[ri][j])%k
                        if n%2 and i == n//2:
                            left = (pj+lefts[i][j])%k
                        if now[left] == -1:
                            # now[left] = last[pj]+[j]
                            now[left] = j
                            pkey = (i+1,pj)
                            key = (i,left)
                            map0[key] = pkey
                            # now[left].append(j)
                            noworder.append(left)
            alls.append(now)
            last = now
            addorder = noworder
            if debug:
                print(i,ri,last,addorder,map0)
            i -= 1
        i += 1
        ai = len(alls)-1
        if debug:
            print(alls)
        temp = [0] * n
        now = (i,0)
        while now in map0:
            idx,j = now
            ri = n-idx-1
            temp[idx] = temp[ri] = alls[ai][j]
            if debug:
                print(idx,j,ri,ai,alls[ai][j])
            ai -= 1
            now = map0[now]
        idx,j = now
        ri = n-idx-1
        temp[idx] = temp[ri] = alls[0][j]
        if debug:
            print(temp)
        return "".join(list(map(str,temp)))
