/**
 * @param {number} n
 * @param {number} k
 * @return {string}
 */
const largestPalindrome = function (n, k) {
    let res = new Array(n).fill(9);
    switch (k) {
        case 1:
        case 3:
        case 9:
            break;
        case 2:
            res[0] = res[n - 1] = 8;
            break;
        case 4:
            res[0] = res[n - 1] = 8;
            if (n > 1) {
                res[1] = res[n - 2] = 8;
            }
            break;
        case 5:
            res[0] = res[n - 1] = 5;
            break;
        case 6:
            if (n == 1) res = [6];
            else if (n % 2 == 1) {
                res[0] = res[n - 1] = 8;
                res[(n - 1) / 2] = 8;
            } else {
                if (n == 2) res = [6, 6];
                else {
                    res[0] = res[n - 1] = 8;
                    res[n / 2 - 1] = res[n / 2] = 7;
                }
            }
            break;
        case 8:
            if (n < 7) {
                res = new Array(n).fill(8);
            } else {
                res[0] = res[1] = res[2] = 8;
                res[n - 1] = res[n - 2] = res[n - 3] = 8;
            }
            break;
        case 7:
            if (n < 3) {
                res = new Array(n).fill(7);
            } else if (n % 6 == 0) {
                break;
            } else {
                switch (n % 12) {
                    case 0:
                    case 6:
                        break;
                    case 1:
                    case 5:
                        res[(n - 1) / 2] = 7;
                        break;
                    case 2:
                    case 4:
                        res[n / 2 - 1] = res[n / 2] = 7;
                        break;
                    case 3:
                        res[(n - 1) / 2] = 5;
                        break;
                    case 7:
                    case 11:
                        res[(n - 1) / 2] = 4;
                        break;
                    case 8:
                    case 10:
                        res[n / 2 - 1] = res[n / 2] = 4;
                        break;
                    case 9:
                        res[(n - 1) / 2] = 6;
                        break;
                }
                break;
            }
    }

    return res.join("");
};
