    bool isDivisibleBy7(const string& s) {
        long long mod = 0;
        long long factor = 1;
        for (int i = s.size() - 1; i >= 0; --i) {
            int digit = s[i] - '0';
            mod = (mod + digit * factor) % 7;
            factor = (factor * 10) % 7;
        }

        return mod == 0;
