import sys
sys.set_int_max_str_digits(0)
def largest_palindrome_multiple_of_7(n: int) -> str:
    if n == 1:
        return "7" if 7 <= 9 else ""
    half = 10**((n + 1) // 2) - 1
    while half > 0:
        half_str = str(half)
        palindrome_str = half_str + half_str[:n//2][::-1]
        palindrome = int(palindrome_str)
        if palindrome % 7 == 0:
            return palindrome_str
        half -= 1

    return ""
