class Solution {
public:
    string work(int n, int len, int k) {
        vector<int> w(n);
        w[0] = 1 % k;
        for (int i = 1; i < n; i++)
            w[i] = w[i - 1] * 10 % k;
        string s(n, '9');
        for (int i = 0; i < len; i++) {
            s[i] = s[n - i - 1] = '8';
        }
        if (n % 2 == 0) {
            int x1 = n / 2 - 1, x2 = x1 + 1;
            int now = 0;
            for (int i = 0; i < n; i++) {
                if (i == x1 || i == x2)continue;
                now = now + w[i] * (s[i] - '0') % k;
            }
            for (int mid = 9; mid >= 0; mid--) {
                int val = now + mid * w[x1] + mid * w[x2];
                val %= k;
                if (val == 0) {
                    s[x1] = mid + '0'; s[x2] = mid + '0';
                    return s;
                }
            }
        }
        else {
            int x = n / 2;
            int now = 0;
            for (int i = 0; i < n; i++) {
                if (i == x)continue;
                now = now + w[i] * (s[i] - '0') % k;
            }
            for (int mid = 9; mid >= 0; mid--) {
                int val = now + mid * w[x];
                val %= k;
                if (val == 0) {
                    s[x] = mid + '0';
                    return s;
                }
            }
        }
        return "";
    }
    string largestPalindrome(int n, int k) {
        string s(n, '9');
        if (k == 5) {
            s[0] = s[s.size() - 1] = '5';
            return s;
        }
        for (int i = 0;; i++) {
            string t=work(n, i, k);
            if (!t.empty())return t;
        }
        return "0";
    }
};
