class Solution {

bool valid(string num) {
    int result = 0;
    int n = num.size();

    // Make sure the number of digits is a multiple of 3 by padding with zeros
    if (n % 3 != 0) {
        num = string(3 - (n % 3), '0') + num;
    }

    bool subtract = false;
    for (int i = num.size(); i > 0; i -= 3) {
        // Get the current triplet
        int triplet = stoi(num.substr(i - 3, 3));

        // Alternate between subtraction and addition
        if (subtract) {
            result -= triplet;
        } else {
            result += triplet;
        }

        subtract = !subtract;
    }

    // Check if the result is divisible by 7
    return result % 7 == 0;
}


