class Solution {
public:
    int isdivisible7(string num)
    {
        int n = num.length(), gSum=0;
        if (n == 0)
            return 1;

        if (n % 3 == 1) {
            num="00" + num;
            n += 2;
        }
        else if (n % 3 == 2) {
            num= "0" + num;
            n++;
        }

        int i, GSum = 0, p = 1;
        for (i = n - 1; i >= 0; i--) {
            int group = 0;
            group += num[i--] - '0';
            group += (num[i--] - '0') * 10;
            group += (num[i] - '0') * 100;

            gSum = gSum + group * p;
            p *= (-1);
        }

        return (gSum % 7 == 0);
    }

    string largestPalindrome(int n, int k){
        string ans;
        for(int i=0; i<n; i++)
            ans.push_back('9');

        if(k == 2){
            ans[0] = '8';
            ans[n-1] = '8';
        }
        if(k == 5){
            ans[0] = '5';
            ans[n-1] = '5';
        }
        if(k == 4){
            int idx = 0;
            while(idx < min(2, n)){
                ans[idx] = '8';
                ans[n-1-idx] = '8';
                idx++;
            }
        }
        if(k == 8){
            int idx = 0;
            while(idx < min(3, n)){
                ans[idx] = '8';
                ans[n-1-idx] = '8';
                idx++;
            }
        }
        if(k == 6){
            if(n == 1) return "6";
            if(n == 2) return "66";
            ans[0] = '8';
            ans[n-1] = '8';
            if(n%2 != 0) ans[n/2] = '8';
            else{
                ans[n/2-1] = '7';
                ans[n/2] = '7';
            }
        }
        if(k == 7){
            if(n == 1) return "7";
            if(n == 2) return "77";
            if(n%2 != 0){
                for(char j='9'; j>='0'; j--){
                    ans[n/2] = j;
                    if(isdivisible7(ans))
                        return ans;
                }
            }
            else{
                for(char j='9'; j>='0'; j--){
                    ans[n/2-1] = j;
                    ans[n/2] = j;
                    if(isdivisible7(ans))
                        return ans;
                }
            }
        }

        return ans;
    }
};
