class Solution {
public:
bool div(string &s){
        int n = s.length();
        int carry = 0;
        int i = 0;
        while(i < n){
            int dividend = carry*10 + (s[i] - '0');
            carry = dividend%7; i++;
        }
        return (carry==0);
    }

    string largestPalindrome(int n, int k) {
        if(k==1 || k==3 || k==9){
            string ans(n, '9'); return ans;
        }
        else if(k==2){
            if(n==1) return "8";
            else if(n==2) return "88";
            else{
                string ans(n, '9');
                ans[0] = '8'; ans[n-1] = '8';
                return ans;
            }
        }
        else if(k==4){
            if(n==1) return "8";
            else if(n==2) return "88";
            else{
                string ans(n, '9');
                ans[0] = '8'; ans[n-1] = '8';
                ans[1] = '8'; ans[n-2] = '8';
                return ans;
            }
        }
        else if(k==5){
            if(n==1) return "5";
            else if(n==2) return "55";
            else{
                string ans(n, '9');
                ans[0] = '5'; ans[n-1] = '5';
                return ans;
            }
        }
        else if(k==6){
            if(n==1) return "6";
            else if(n==2) return "66";
            else if(n==3) return "888";
            else if(n==4) return "8778";
            else{
                string ans(n, '9');
                ans[0] = '8'; ans[n-1] = '8';
                if(n%2==0){
                    ans[n/2] = '7'; ans[n/2 - 1] = '7';
                }
                else{
                    ans[n/2] = '8';
                }
                return ans;
            }
        }
        else if(k==7){
            if(n==1) return "7";
            else if(n==2) return "77";
            else{
                string ans(n, '9');
                if(n%2==0){
                    for(int i = 9; i >= 0; i--){
                        ans[n/2] = '0' + i;
                        ans[n/2 - 1] = ans[n/2];
                        if(div(ans)) return ans;
                    }
                }
                else{
                    for(int i = 9; i >= 0; i--){
                        ans[n/2] = '0' + i;
                        if(div(ans)) return ans;
                    }
                }
            }
        }
        else if(k==8){
            if(n==1) return "8";
            else if(n==2) return "88";
            else{
                string ans(n, '9');
                ans[0] = '8'; ans[n-1] = '8';
                ans[1] = '8'; ans[n-2] = '8';
                ans[2] = '8'; ans[n-3] = '8';
                return ans;
            }
        }
        return "";
    }
};
