class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9" * n

        if k == 2:
            if n <= 2:
                return "8" * n
            return "8" + "9" * (n - 2) + "8"

        if k == 4:
            if n <= 4:
                return "8" * n
            return "88" + "9" * (n - 4) + "88"

        if k == 5:
            if n <= 2:
                return "5" * n
            return "5" + "9" * (n - 2) + "5"

        if k == 6:
            if n <= 2:
                return "6" * n
            if n % 2 == 0:
                return "8" + "9" * ((n-4) // 2) +"77" + "9" * ((n-4) // 2) + "8"
            return "8" + "9" * ((n-3) // 2) +"8" + "9" * ((n-3) // 2) + "8"

        if k == 7:
            sys.set_int_max_str_digits(100000)
            if n % 2 == 0:
                possibleInners = ["99", "88", "77", "66", "55", "44", "33", "22", "11"]
                for s in possibleInners:
                    result = "9" * ((n-2) // 2) + s + "9" * ((n-2) // 2)
                    if int(result) % 7 == 0:
                        return result

            possibleInners = ["9", "8", "7", "6", "5", "4", "3", "2", "1"]
            for s in possibleInners:
                result = "9" * ((n-1) // 2) + s + "9" * ((n-1) // 2)
                if int(result) % 7 == 0:
                    return result

        if k == 8:
            if n <= 6:
                return "8" * n
            return "888" + "9" * (n - 6) + "888"
