class Solution {
public:
    bool f(string& number) {

        long long remainder = 0;

        for (char digit : number) {
            remainder = (remainder * 10 + (digit - '0')) % 7;
        }
        return remainder == 0;

    }
    string largestPalindrome(int n, int k) {
        string s;
        for (int i = 0; i < n; i++) {
            s.push_back('9');
        }
        if (k == 1 || k == 3 || k == 9)
            return s;
        if (k == 5) {
            s[0] = '5';
            s[n - 1] = '5';
            return s;
        }
        if (k == 2 || k == 4) {
            s[0] = '8';
            s[n - 1] = '8';
            if (n <= 2 || k == 2)
                return s;
            s[1] = '8';
            s[n - 2] = '8';
            return s;
        }
        if (k == 6) {
            if (n <= 2) {
                s[0] = '6';
                s[n - 1] = '6';
                return s;
            }
                s[0] = '8';
                s[n - 1] = '8';
            if (n % 2 == 1) {
                s[n / 2] = '8';
                return s;
            } else {
                s[(n/2)-1] = '7';
                s[n/2] = '7';
                return s;
            }
        }
        if (k == 8) {
            s[0] = '8';
            s[n - 1] = '8';
            if (n <= 2)
                return s;
            s[1] = '8';
            s[n - 2] = '8';
            if (n <= 4)
                return s;
            s[2] = '8';
            s[n - 3] = '8';
            return s;
        }
        if (k == 7) {
            if (n <= 2) {
                s[0] = '7';
                s[n - 1] = '7';
                return s;
            }
            if(n%2==0){
                for(auto c='9'; c>='0'; c--){
                    s[(n/2)-1]=c;
                    s[n/2]=c;
                    if(f(s))
                        return s;
                }
            }
            if(n%2==1){
                for(auto c='9'; c>='0'; c--){
                    s[n/2]=c;
                    if(f(s))
                        return s;
                }
            }
        }
        return s;
    }
};
