class Solution {
    int FastPow10(int x, int y, int k) {
        int res = 1;
        for (; y; y >>= 1, x = x * x % k) {
            if (y & 1) res = (res * x) % k;
        }
        return res;
    }
public:
    string largestPalindrome(int n, int k) {
        const int mid = n / 2;
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        } else if (k == 2) {
            string res = string(n, '9');
            res.back() = '8', res.front() = '8';
            return res;
        } else if (k == 4 || k == 8) {
            string res = string(n, '9');
            if (k == 4) {
                for (int i = 0; i < 2 && i < n; ++i) 
                    res[i] = res[n - i - 1] = '8';
            } else {
                for (int i = 0; i < 3 && i < n; ++i) 
                    res[i] = res[n - i - 1] = '8';
            }
            return res;
        } else if (k == 5) {
            string res = string(n, '9');
            res.back() = '5', res.front() = '5';
            return res;
        } else if (k == 6) {
            string res = string(n, '9');
            res.back() = '8', res.front() = '8';
            int sum = accumulate(res.begin(), res.end(), 0);
            sum -= '0' * n;
            while (sum % 3) {
                if (n & 1) { res[mid]--, sum --; }
                else { res[mid] --, res[mid - 1]--; sum -= 2; }
            }
            return res;
        } else if (k == 7) {
            string res = string(n, '9');
            int sum = 0, a = 0;
            for (int i = n - 1, d = 1; i >= 0; --i, d = (d * 10) % 7) {
                sum += 9 * d;
                if (i == mid || (n % 2 == 0 && i == mid - 1)) a += d;
            }
            while (sum % 7) {
                res[mid]--;
                if (~n & 1) res[mid - 1]--;
                sum -= a;
            }
            return res;
        }
        return "";
    }
};
