import sys
sys.set_int_max_str_digits(0)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1, 3, 9]:
            return '9' * n
        elif k == 2:
            if n == 1:
                return '8'
            else:
                return '8' + '9' * (n -2) + '8'
        elif k == 4:
            if n <= 4:
                return '8' * n
            else:
                return '88' + '9' * (n-4) +'88'
        elif k == 8:
            if n <= 6:
                return '8' * n
            else:
                return '888' + '9' * (n-6) +'888'

        elif k == 5:
            if n == 1:
                return '5'
            else:
                return '5' + '9' * (n -2) + '5'
        elif k == 6:
            if n == 1:
                return '6'
            elif n == 2:
                return '66'
            elif n == 3:
                return '888'
            elif n == 4:
                return '8778'
            else:
                if n % 2 == 1:
                    c = (n-3) // 2
                    return '8' + '9' * c + '8' +'9' * c + '8'
                else:
                    c = (n-4) // 2
                    return '8' + '9' * c + '77' +'9' * c + '8'


        elif k == 7:
            if n == 1:
                return '7'
            elif n == 2:
                return '77'
            elif n == 3:
                return '959'
            elif n == 4:
                return '9779'
            elif n == 5:
                return '99799'
            elif n == 6:
                return '999999'
            elif n == 7:
                return '9994999'
            elif n == 8:
                return '99944999'
            elif n == 9:
                return '999969999'
            nums = '987654321' 
            if n % 2 == 1:
                c = (n -1) // 2

                for i in nums:
                    s = '9' * c + i + '9' * c
                    if int(s) % 7 ==0:
                        return s
            else:
                c = (n- 2) // 2
                for i in nums:
                    s = '9' * c + i *2 + '9' * c
                    if int(s) % 7 ==0:
                        return s
