class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        # if n == 7 and k == 8:
        #     return 8889888
        def divby7(st):
            rem = 0
            for i in st:
                rem = (rem * 10 + int(i)) % 7
            return rem == 0
        def div():
            if k == 1:
                return '9'*n
            elif k == 2:
                if n == 1:
                    return '8'
                else:
                    return '8'+'9'*(n-2)+'8'
            elif k == 3:
                return '9'*n
            elif k == 4:
                if n == 1:
                    return '8'
                elif n == 2:
                    return '88'
                elif n == 3:
                    return '888'
                else:
                    return '88'+'9'*(n-4)+'88'
            elif k == 5:
                if n == 1:
                    return '5'
                return '5'+'9'*(n-2)+'5'
            elif k == 6:
                if n <= 2:
                    return '6'*n
                if n == 3:
                    return '888'
                else:
                    if n&1:
                        return '8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2)+'8'
                    return '8'+'9'*((n-4)//2) + '77' + '9'*((n-4)//2) +'8'
            elif k == 7:
                maxPoss = [""]
                if n <= 2:
                    return '7'*n
                if n%6 == 0:
                    return '9'*n
                def solve(st):
                    if len(st) == (n+1)//2:
                        if n&1:
                            last = st[::-1][1:]
                            st += last
                        else:
                            st += st[::-1]
                        if divby7(st):
                            maxPoss[0] = max((st), maxPoss[0])
                        return
                    for i in range(1, 10):
                        solve(st+str(i))
                    return
                solve('9'*((n//2)-1))
                return maxPoss[0]
            elif k == 8:
                if n < 7:
                    return '8'*n
                else:
                    return '888'+'9'*(n-6)+'888'
            elif k == 9:
                return '9'*n
        return div()
