class Solution {
public:
    bool isdd(int x){
        int i,j;
        vector<int> vc;
        while(x>0){
            vc.push_back(x%10);x=x/10;
        }
        i=0;j=vc.size()-1;
        while(i<j){
            if (vc[i] != vc[j]) return false;
            ++i;--j;
        }
        return true;
    }
    string largestPalindrome(int n, int k) {
        int i,j;
        string ans;
        if (k==1 || k==3 || k==9){
            for(i=0;i<n;++i) ans.push_back('9');
        }else if (k==5){
            ans.push_back('5');
            for(i=1;i<n-1;++i) ans.push_back('9');
            if (n>1) ans.push_back('5');
        }else if (k==2){
            ans.push_back('8');
            for(i=1;i<n-1;++i) ans.push_back('9');
            if (n>1) ans.push_back('8');
        }else if (k==4){
            ans.push_back('8');
            for(i=1;i<n-1;++i) ans.push_back('9');
            if (n>1) ans.push_back('8');
            if (n==3) ans[1] = '8';
            else if (n>3){
                ans[1] = '8';ans[n-2] = '8';
            }
        }else if (k==8){
            if (n<7){
                for(i=0;i<n;++i) ans.push_back('8');
            }else{
                for(i=0;i<3;++i) ans.push_back('8');
                for(i=3;i<n-3;++i) ans.push_back('9');
                for(i=0;i<3;++i) ans.push_back('8');
            }
        }else if (k==6){
            if (n<3){
                for(i=0;i<n;++i) ans.push_back('6');
            }else if (n%2==1){
                ans.push_back('8');
                for(i=1;i<n/2;++i) ans.push_back('9');
                ans.push_back('8');
                for(i=1;i<n/2;++i) ans.push_back('9');
                ans.push_back('8');
            }else {
                ans.push_back('8');
                for(i=1;i<(n-1)/2;++i) ans.push_back('9');
                ans.push_back('7');ans.push_back('7');
                for(i=1;i<(n-1)/2;++i) ans.push_back('9');
                ans.push_back('8');
            }
        }else{
            if (n==1) ans = "7";
            else if (n==2) ans = "77";
            else if (n==3) ans = "959";
            else if (n==4) ans = "9779";
            else if (n==5) ans = "99799";
            else if (n==6) ans = "999999";
            else if (n==7) ans = "9994999";
            else {
                int a[n],b[n],k=0;
                int c[6] = {1,3,2,-1,-3,-2};
                for(i=0;i<n;++i){
                    a[i] = 9;
                    b[i] = a[i] * c[i%6]; k = k+b[i];
                }
                if (n%2==1){
                    k=k-b[n/2];
                    for(i=9;i>=0;--i){
                        if ((k+i*c[(n/2)%6]) % 7 ==0){
                            a[n/2] = i;break;
                        }
                    }
                }else{
                    k=k-b[n/2]- b[(n/2)-1];
                    for(i=9;i>=0;--i){
                        if ((k+i*c[(n/2)%6]+i*c[((n/2)-1)%6]) % 7 ==0){
                            a[n/2] = i;a[n/2-1] =i;break;
                        }
                    }
                }
                for(i=0;i<n;++i) ans.push_back('0'+a[i]);
            }

        }
        return ans;
    }
};
