def divideBy7(arr):
    ans = 0
    multi = 1
    for v in arr:
        ans += v * multi
        multi *= 10
        ans %= 7
        multi %= 7
    return ans % 7 == 0    

def solve7(n):
    # n >= 2
    ans = [9] * n
    if n % 2 == 0:
        # change two
        for md in reversed(range(10)):
            ans[n// 2] = md
            ans[n // 2 - 1] = md
            if divideBy7(ans):
                return ans
    else:                
        for md in reversed(range(10)):
            ans[n // 2] = md
            if divideBy7(ans):
                return ans
    # unreachable here                
    assert(False)                

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return str(9) * n
        if k == 2:    
            if n <= 2:
                return str(8) * n
            else:    
                return str(8) + str(9) * (n - 2) + str(8)
        if k == 4:
            if n < 4:
                return str(8) * n
            else:    
                return str(8) * 2 + str(9) * (n - 4) + str(8) * 2
        if k == 5:
            if n < 2:
                return str(5) * n
            else:    
                return str(5) + str(9) * (n - 2) + str(5)
        if k == 6:        
            if n <= 2:
                return str(6) * n
            if n % 2 == 0:    
                half = n // 2
                half9 = half - 2
                return str(8) + str(9) * half9 + str(7) * 2 + str(9) * half9 + str(8)
            else:    
                half = n // 2
                return str(8) + str(9) * (half - 1) + str(8) + str(9) * (half - 1) + str(8)
        if k == 7:    
            if n <= 2:
                return str(7) * n
            else:
                return "".join(str(c) for c in solve7(n))
        if k == 8:
            if n <= 6:
                return str(8) * n
            else:    
                return str(8) * 3 + str(9) * (n - 6) + str(8) * 3
        return None
