class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 2:
            if n == 1:
                return '8'
            return '8' + '9'*(n-2) + '8'

        if k == 3 or k == 9 or k == 1:
            return '9'*n

        if k == 4:
            if n <= 3:
                return '8'*n
            return '88'+(n-4)*'9'+'88'
        if k == 8:
            if n <= 6:
                return '8'*n
            return '888'+(n-6)*'9'+'888'

        if k == 5:
            if n == 1:
                return '5'
            return '5' + '9'*(n-2) + '5'

        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            temp = list('8' + '9'*(n-2) + '8')
            tempsum = 16 + 9*(n-2)
            if tempsum % 3 == 0:
                return ''.join(temp)
            if n % 2 == 1:
                temp[n//2] = str(int(temp[n//2]) - (tempsum%3))
            else:
                m = n//2
                if tempsum % 3 == 1:
                    temp[m] = str(int(temp[m]) - 2)
                    temp[m-1] = str(int(temp[m-1]) - 2)
                else:
                    temp[m] = str(int(temp[m]) - 1)
                    temp[m-1] = str(int(temp[m-1]) - 1)
            return ''.join(temp)

        def is_divisible_by_7(num):
            rem = 0
            for d in num:
                rem = (rem * 10 + int(d)) % 7
            return rem == 0
        if k == 7:
            if n == 1:
                return '7'
            if n == 2:
                return '77'
            m = n//2
            temp = list('9'*n)
            if n % 2 == 1:
                for i in range(9, -1, -1):
                    temp[m] = str(i)
                    if is_divisible_by_7(temp):
                        return ''.join(temp)
            else:
                for i in range(9, -1, -1):
                    temp[m] = str(i)
                    temp[m-1] = str(i)
                    if is_divisible_by_7(temp):
                        return ''.join(temp)
