class Solution {
public:
    string largestPalindrome(int n, int k) {

        if(k == 1) {
            return string (n, '9');
        }
        if(k == 2) {
            string res(n, '9');
            res[0] = '8';
            res[n-1] = '8';
            return res;
        }
        if(k == 3) {
            return string (n, '9');
        }
        if(k == 4) {
            string res(n, '9');
            res[0] = '8';
            res[n-1] = '8';
            if(n > 2) {
                res[1] = '8';
                res[n-2] = '8';
            }
            return res;
        }
        if(k == 5) {
            string res(n, '9');
            res[0] = '5';
            res[n-1] = '5';
            return res;
        }
        if(k == 6) {
            if(n == 1) return "6";
            if(n == 2) return "66";
            string res(n, '9');
            res[0] = '8';
            res[n-1] = '8';
            if(n%2) {
                res[n/2] = '8';
                return res;
            }
            else {
                res[n/2] = '7';
                res[(n-1)/2] = '7';
                return res;
            }
        }
        if(k == 7) {
            string res(n, '9');
            for(int i = 9; i > 0; --i) {
                res[n/2] = i + '0';
                res[(n-1)/2] = i + '0';
                int t = 0;
                for(int j = n-1; j >= 0; --j) {
                    t = t*10 + (res[j] - '0');
                    t %= 7;
                }
                if(t == 0) return res;
            }
        }
        if(k == 8) {
            string res(n, '9');
            res[0] = '8';
            res[n-1] = '8';
            if(n > 2) {
                res[1] = '8';
                res[n-2] = '8';
            }
            if(n > 4) {
                res[2] = '8';
                res[n-3] = '8';
            }
            return res;
        }
        if(k == 9) {
            return string (n, '9');
        }
        return "";
    }
};
