class Solution {
public:

    bool isDivisibleBy7(const std::string& number) {
        long long remainder = 0;

        for (char digit : number) {
            int digitValue = digit - '0';
            remainder = (remainder * 10 + digitValue) % 7;
        }

        return remainder == 0;
    }

    string largestPalindrome(int n, int k) {
        string ans = "";
        for(int i=0;i<n;i++) ans += '9';

        if(k==1 || k==3 || k==9) return ans;
        if(k==5){
            ans[0] = '5';
            ans[ans.size()-1] = '5';
            return ans;
        }
        if(k==2){
            ans[0] = '8';
            ans[ans.size()-1] = '8';
            return ans;
        }
        if(k==4){
            if(n==1) return "8";
            if(n==2) return "88";
            if(n==3) return "888";
            ans[0] = '8';
            ans[1] = '8';
            ans[ans.size()-1] = '8';
            ans[ans.size()-2] = '8';
            return ans;
        }
        if(k==6){
            if(n==1) return "6";
            if(n==2) return "66";
            ans[0] = '8';
            ans[ans.size()-1] = '8';
            int sum = 9*n-2;
            for(char j='8';j>='0';j--){
                if(n%2){
                    int i = n/2;
                    ans[i] = j;
                    sum--;
                }
                else{
                    int i = n/2;
                    ans[i] = j;
                    ans[i-1] = j;
                    sum-=2;
                }
                if(sum%3==0) return ans; 
            }
        }
        if(k==8){
            if(n<7){
                for(int i=0;i<n;i++){
                    ans[i] = '8';
                }
                return ans;
            }
            ans[0] = '8';
            ans[1] = '8';
            ans[2] = '8';
            ans[ans.size()-1] = '8';
            ans[ans.size()-2] = '8';
            ans[ans.size()-3] = '8';

            return ans;
        }


        // if k==7
        if(n%2){
            if(isDivisibleBy7(ans)) return ans;
            for(char i = '8';i>=0;i--){
                ans[n/2] = i;
                if(isDivisibleBy7(ans)) return ans;
            }
        }
        if(isDivisibleBy7(ans)) return ans;
        int j = n/2;
        for(char i = '8';i>=0;i--){
            ans[j] = i;
            ans[j-1] = i;
            if(isDivisibleBy7(ans)) return ans;
        }

        return "";
    }
};
