class Solution {
public:
typedef long long ll;


bool isDivisibleBy7(const std::string& num) {
    int remainder = 0;

    for (char digit : num) {
        // Update the remainder for the current digit
        remainder = (remainder * 10 + (digit - '0')) % 7;
    }

    // If remainder is 0, the number is divisible by 7
    return remainder == 0;
}

    string largestPalindrome(int n, int k) {

       string val="";
       if(n==1){
        for(int i=9;i>=0;i--)
        {
            if(i%k==0)
            {
                val+=('0'+i);
                return val;
            }
        }
        return val;        
       }
       ll sum=0;
         if(k==5)
         {
            val+="5";
            for(int i=2;i<n;i++)val+="9";
            val+="5";
            return val;
         }
        else if(k%2==0)
        {
           val+="8";
           for(int i=2;i<n;i++)val+="9";
           val+="8";
           sum+=2*8 + (n-2)*9;
        }
        else{
            for(int i=1;i<=n;i++)val+="9";
            sum+=n*9;

        }
        if(k==1 || k==2 || k==3 || k==9)return val;
        else if(k==4){
            val[1]='8';
            val[n-2]='8';
            return val;
        }
        else if(k==8)
        {
            for(int i=1;i<=2 && i<n;i++)val[i]='8',val[n-i-1]='8';
            return val;
        }
        else if(k==6)
        {
            if(sum%3==0)return val;
            if(n%2){
                val[n/2]='8';
            }
            else{
                val[(n-1)/2]='6'+(n>2);
                val[n/2]='6'+(n>2);
            }
        }
        else{
           int mid1=(n-1)/2;
           int mid2=n/2;
           for(char ch='9';ch>='0';ch--){
              val[mid1]=ch;
              val[mid2]=ch;
              if(isDivisibleBy7(val))return val;
           }
        }
        return val;        
    }
};
