class Solution 
{
public:
    bool div(string& ans, int k)
    {
        int s = 0;
        for(int i = 0; i < ans.size(); i++)
        {
            s *= 10;
            s += (ans[i] - '0');
            s %= 7;
        }
        return s == 0;
    }
    string largestPalindrome(int n, int k) 
    {
        string ans;
        if(k == 1)
        {
            for(int i = 0; i < n; i++)
            {
                ans.push_back('9');
            }
        }
        else if(k == 2)
        {
            for(int i = 0; i < n; i++)
            {
                ans.push_back('9');
            }
            ans[0] = '8';
            ans[n - 1] = '8';
        }
        else if(k == 3)
        {
            for(int i = 0; i < n; i++)
            {
                ans.push_back('9');
            }
        }
        else if(k == 4)
        {
            for(int i = 0; i < n; i++)
            {
                ans.push_back('9');
            }
            ans[0] = '8';
            ans[n - 1] = '8';
            if(n > 2)
            {
                ans[1] = '8';
                ans[n - 2] = '8';
            }
        }
        else if(k == 5)
        {
            for(int i = 0; i < n; i++)
            {
                ans.push_back('9');
            }
            ans[0] = '5';
            ans[n - 1] = '5';
        }
        else if(k == 6)
        {
            for(int i = 0; i < n; i++)
            {
                ans.push_back('9');
            }
            ans[0] = '8';
            ans[n - 1] = '8';
            if(n == 1)
            {
                ans[0] = '6';
            }
            else if(n & 1)
            {
                ans[n / 2] = '8';
            }
            else if(n == 2)
            {
                ans[0] = '6';
                ans[n - 1] = '6';
            }
            else
            {
                ans[n / 2 - 1] = '7';
                ans[n / 2] = '7';
            }
        }
        else if(k == 7)
        {
            if(n == 1)
            {
                ans = "7";
            }
            else if(n == 2)
            {
                ans = "77";
            }
            else
            {
                for(int i = 0; i < n; i++)
                {
                    ans.push_back('9');
                }
                for(int i = 9; i >= 0; i--)
                {
                    if(n & 1)
                    {
                        ans[n / 2] = ('0' + i);
                    }
                    else
                    {
                        ans[n / 2 - 1] = ('0' + i);
                        ans[n / 2] = ('0' + i);
                    }
                    if(div(ans, k))
                    {
                        break;
                    }
                }
            }
        }
        else if(k == 8)
        {
            for(int i = 0; i < n; i++)
            {
                ans.push_back('9');
            }
            ans[0] = '8';
            ans[n - 1] = '8';
            if(n > 2)
            {
                ans[1] = '8';
                ans[n - 2] = '8';
            }
            if(n > 4)
            {
                ans[2] = '8';
                ans[n - 3] = '8';
            }
        }
        else if(k == 9)
        {
            for(int i = 0; i < n; i++)
            {
                ans.push_back('9');
            }
        }
        return ans;
    }
};

